import {ACTION_TYPE} from "./actionType";
import InStreamAdsApi from "../apis/inStreamAdsApi";


export const setInStreamAds = (item) =>{
  return dispatch => {
    return InStreamAdsApi.setInStreamAds(item).then(res => {
      const result = {
        type: ACTION_TYPE.INSTREAM_ADS,
        payload: res
      }
      dispatch(result)
      return res
    })
  }
}

export const getListInStreamAds = (id) =>{
  return dispatch => {
    return InStreamAdsApi.getListInStreamAds(id).then(res => {
      const result = {
        type: ACTION_TYPE.LIST_INSTREAM_ADS,
        payload: res
      }
      dispatch(result)
      return res
    })
  }
}
export const getListAllInStreamAds = (id) =>{
  return dispatch => {
    return InStreamAdsApi.getListAllInStreamAds(id).then(res => {
      const result = {
        type: ACTION_TYPE.LIST_ALL_INSTREAM_ADS,
        payload: res
      }
      dispatch(result)
      return res
    })
  }
}
export const getListInStreamAdsById = (id) =>{
  return dispatch => {
    return InStreamAdsApi.getListInStreamAdsById(id).then(res => {
      const result = {
        type: ACTION_TYPE.LIST_INSTREAM_ADS_ID,
        payload: res
      }
      dispatch(result)
      return res
    })
  }
}
export const deleteInStreamAdsById = (id) =>{
  return dispatch => {
    return InStreamAdsApi.deleteInStreamAdsById(id).then(res => {
      const result = {
        type: ACTION_TYPE.DELETE_INSTREAM_ADS_ID,
        payload: res
      }
      dispatch(result)
      return res
    })
  }
}
