export function parsedPageLimit  (total, rowPerPage) {
  let max = Math.floor(total / rowPerPage)
  if (total % rowPerPage !== 0) {
    max++;
  }
  if (max === 0) {
    max = 1;
  }
  return max
}

function parseContentList (items) {
  if (!items || items.length === 0) return []
  return items
}

function saveAccessToken (accessToken) {
  //time expire token
  const timeNow = new Date()
  timeNow.setFullYear(timeNow.getFullYear() + 1)
  //save access token
  const option = {
    expires: timeNow,
    // domain: 'http://localhost:3000',
    path: '/'
  }

  try {
    const key = ConfigCookie.getTokenKey()
    const type = ConfigCookie.METHOD.SAVE
    const value = accessToken
    ConfigCookie.handleCookie({ type, key, value, option })
    ConfigCookie.handleCookie({
      type: ConfigCookie.METHOD.REMOVE,
      key: ConfigCookie.KEY.ANONYMOUS_TOKEN,
      option
    })
    handleLocalStorage(LocalStorage.SET, LocalStorage.RE_LOGIN, true)
    handleLocalStorage(LocalStorage.SET, 'Token', accessToken)
  } catch (e) {
    throw e
  }
}
async function removeAccessToken (router, notRefresh) {
  try {
    await ConfigCookie.remove(ConfigCookie.KEY.ACCESS_TOKEN)
    await ConfigCookie.remove(ConfigCookie.KEY.ACCESS_TOKEN_TEST)
    await ConfigCookie.remove(ConfigCookie.KEY.ACCESS_TOKEN_DEV)
    await ConfigCookie.remove(ConfigCookie.KEY.ANONYMOUS_TOKEN)
  } catch (e) {
    throw e
  }

  if (typeof window === 'undefined') return

  if (!notRefresh) {
    window.location.reload()
  }
}

const classUseStyles = (theme) => ({
  root: {
    "& .MuiInputLabel-outlined:not(.MuiInputLabel-shrink)": {
      transform: "transparent"
    },
    "& .MuiFormLabel-root": {
      color: '#222222',
      fontSize: "12.5px"
    }
    ,"& .MuiSvgIcon--root": {
      color: '#636f83',
    }
    ,"& .MuiInputLabel-root": {
      textOverflow: 'ellipsis',
      whiteSpace: 'nowrap',
      maxWidth: '50%',
      overflow: 'hidden',
    }
  },
  inputRoot: {
    color: "#222222",
    fontSize: "12.5px",
    '&[class*="MuiOutlinedInput-root"] .MuiAutocomplete-input:first-child': {
      paddingLeft: 5.25
    },
    "& .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent"
    },
    "&:hover .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent"
    },
    "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent"
    }
  }
})



export {
  sendToast,
  setUrlParams,
  validateEmail,
  saveAccessToken,
  handleLocalStorage,
  json_to_query_string,
  parsedTimeCreate,
  parseContentList,
  parsedIdContentProvider,
  parsedIdAdsGroups,
  parsedNumberUrl,
  classUseStyles,
  removeAccessToken
}
